layui.use('layer', function(){
	  var layer = layui.layer;
	 layer.confirm('纳尼？', {
	 btn: ['按钮一', '按钮二', '按钮三'] //可以无限个按钮 
	    ,btn3: function(index, layero){
	   //按钮【按钮三】的回调
	    }
	},
	 function(index, layero){    //按钮【按钮一】的回调
	    },
	 function(index){   //按钮【按钮二】的回调
	    })
	});$END$

layui.use('layer', function(){
	  var layer = layui.layer;
	  layer.prompt(function(val, index){
	      layer.msg('得到了'+val);
	      layer.close(index);
	   })
	});$END$

layui.use('layer', function(){
	  var layer = layui.layer;
	 layer.load(1);
	});$END$

layui.use('layer', function(){
	  var layer = layui.layer;
	    layer.tab({
	          area: ['600px', '300px'],
	         tab: [{
	                    title: 'TAB1', 
	                    content: '内容1'
	        }, {
	        title: 'TAB2', 
	       content: '内容2'
	    }, {
	        title: 'TAB3', 
	        content: '内容3'
	    }]
	  });   
	});$END$

layui.use('layer', function(){
